function getNewsFeed(n){var i="#newsLoading",t="#rssRow",r=150;$(i).show();$(t).hide();$.ajax({url:"/newsfeed/all/"+n,type:"GET",dataType:"json",success:function(n){var f=$(t),u;f.empty();u={rssNews:[]};n.forEach(function(n){n.truncatedDetails=jQuery.trim(n.details).substring(0,r);n.restDetails=n.details.substring(r);u.rssNews.push(n);animatedcollapse.addDiv("detailsContent"+n.feedId,"fade=1")});$("#rssTmpl").template(u).appendTo(f);animatedcollapse.init();$(t).show();$(i).hide()},error:function(n){console.log(n.message)}})}(function(){$("#newsLoading").hide();$.ajax({url:"/newsfeed/navConfig",type:"GET",dataType:"json",success:function(n){var t=n.totalItemsCount,i=n.itemsPerPageCount;$("#feedPagination").pagination(t,{num_edge_entries:2,num_display_entries:8,items_per_page:i,load_first_page:!0,callback:function(n){getNewsFeed(n+1)}})}})})(jQuery);